INSERT INTO `sm_contents` (`content_id`, `parent_content_id`, `user_id`, `title`, `slug`, `body`, `thumb`, `excerpt`, `is_active`, `path`, `content_type`, `updated_timestamp`, `insert_timestamp`) VALUES
(31, 0, 1, 'Voucher Terms and Conditions', 'voucher_terms_and_conditions', '<ol>\n<li>This voucher is valid for one (1) year from date of purchase.</li>\r\n<li>Pre-reservation is required at least 3 days in advance.</li>\r\n<li>Booking fee is Php 500.00.</li>\r\n<li>Breakfast is not included. You can avail breakfast for four (4) pax at Php 1,000 only using your fund and made together with the room reservation.</li>\r\n<li>Notice of cancellation should be made one (1) week prior to arrival. Otherwise, this voucher will be forfeited.</li>\r\n<li>Failure to check-in on the scheduled date of arrival will also forfeit this voucher.</li>\r\n<li>To extend the validity of the voucher for another 6 months, please pay extension fee of Php 500.00.</li>\r\n<li>Reservations not guaranteed will be hold only until 6:00PM otherwise the reservation will be subject to room availability.</li>\r\n<li>Airport transfers are not included.</li>\r\n<li>Check-in time is 2:00 pm. Check out time is 12:00 pm.</li>\r\n<li>Any voucher can be transferred once by the member. This can be made through the CrownLifestyle.Net portal. A new voucher shall be sent to the endorsing member or the email of the voucher assignee.</li>\n</ol>', '', NULL, 0, '', '', '0000-00-00 00:00:00', '2012-11-22 14:53:00'),
(32, 0, 1, 'Voucher Footer', 'voucher_footer', 'Head Office: Suite 1902, The Centerpoint Julia Vargas Ave., corner Garnet Road, Ortigas Center, Pasig City 1605 PH • Direct Line: +63 (32) 631-1899 * • Email:\nOrtigas Depot: Unit G 4, 5 & 6 Ground Floor,BSA Twin Towers, Bank Drive, Ortigas Center,Mandaluyong City PH • Direct Line: +63 (32) 654-4893 / 96\nCebu Depot: Unit G 11-13 Ground Floor, Tower Mall, Fuente Tower 3, Osmeña Blvd., Cebu City 6000 Philippines • Direct Line: (6332) 418-2927; 418-7777\nDavao Depot: Sobrecarey Street (back of McDonald’s Bajada) Bo. Obrero, Davao City Philippines • Direct Line: (6382) 227-3069 | (6382) 227-8482', '', NULL, 0, '', '', '0000-00-00 00:00:00', '2012-11-23 12:22:24');
